    # Hàm tải dữ liệu lên cho table
    def loaddataTable(self):
        sqlquery = "select * from giangvien order by cast(substring(MaGV,3,10) as int)"
        self.tableWidget.setRowCount(50)
        tablerow = 0
        # đặt số cột, và dữ liệu ở mỗi cột
        for row in ketnoi().execute(sqlquery):
            self.tableWidget.setItem(tablerow, 0, QtWidgets.QTableWidgetItem(row[0]))
            self.tableWidget.setItem(tablerow, 1, QtWidgets.QTableWidgetItem(row[1]))
            self.tableWidget.setItem(tablerow, 2, QtWidgets.QTableWidgetItem(row[2]))
            self.tableWidget.setItem(tablerow, 3, QtWidgets.QTableWidgetItem(row[3]))
            self.tableWidget.setItem(tablerow, 4,
                                     QtWidgets.QTableWidgetItem("Nam" if str(row[4]).__eq__("True") else "Nữ"))
            self.tableWidget.setItem(tablerow, 5, QtWidgets.QTableWidgetItem(row[5]))
            self.tableWidget.setItem(tablerow, 6, QtWidgets.QTableWidgetItem(row[6]))
            self.tableWidget.setItem(tablerow, 7, QtWidgets.QTableWidgetItem(row[7]))
            self.tableWidget.setItem(tablerow, 8, QtWidgets.QTableWidgetItem(
                "Đã nghỉ" if str(row[8]).__eq__("True") else "Đang làm việc"))

            tablerow += 1
        self.tableWidget.setRowCount(tablerow)
        # Khong cho edit
        self.tableWidget.setEditTriggers(QtWidgets.QTableWidget.NoEditTriggers)

        # Sửa kích cỡ cột
        self.tableWidget.setColumnWidth(1, 150)

    # Hàm bắt event table click tải dữ liệu lên ô để sửa
    def tableClick(self):
        self.pushButton_9.setEnabled(False)
        self.pushButton_10.setEnabled(True)
        self.pushButton_11.setEnabled(True)
        row = self.tableWidget.currentRow()
        self.label.setText(self.tableWidget.item(row, 0).text())
        self.HTLineEdit.setText(self.tableWidget.item(row, 1).text())
        self.hCVComboBox.setCurrentText(self.tableWidget.item(row, 2).text())
        self.hCHMComboBox.setCurrentText(self.tableWidget.item(row, 3).text())
        if self.tableWidget.item(row, 4).text() == "Nam":
            self.namCheckBox.setChecked(True)
        else:
            self.namCheckBox.setChecked(False)
        self.ngYSinhDateEdit_2.setDate(QtCore.QDate(int((str(self.tableWidget.item(row, 5).text())[0:4])),
                                                    int((str(self.tableWidget.item(row, 5).text())[5:7])),
                                                    int((str(self.tableWidget.item(row, 5).text())[8:10]))))
        self.aChLineEdit_2.setText(self.tableWidget.item(row, 6).text())
        self.chuyNMNLineEdit_2.setText(self.tableWidget.item(row, 7).text())

        if self.tableWidget.item(row, 8).text() == "Đang làm việc":
            self.angLMViCCheckBox_2.setChecked(True)
        else:
            self.angLMViCCheckBox_2.setChecked(False)

        self.loaddataListMH()
        self.loaddataListMHChon()

    def taoMaGV(self):
        sqlquery = "select maGV from giangvien"
        max = 0
        for row in ketnoi().execute(sqlquery):
            b = str(row).replace("(", "", 2)
            c = str(b).replace("'", "", 2)
            d = str(c).replace(",", "", 2)
            e = str(d).replace(")", "", 2)
            ma = int((str(e)[2:4]))
            if ma > max:
                max = ma
        return "GV" + str(max + 1)

