    # Hàm chuyển tên Môn sang mã Môn
    def searchMaMH(self, a):
        sqlquery = "SELECT MaMH FROM MonHoc WHERE TenMH=N'" + a + "'"
        for row in ketnoi().execute(sqlquery):
            b = str(row).replace("(", "", 2)
            c = str(b).replace("'", "", 2)
            d = str(c).replace(",", "", 2)
            e = str(d).replace(")", "", 2)
        return e

