    # Hàm tải dữ liệu lên cho table
    def loaddataTable(self):
        sqlquery="select * from loptinchi order by cast(substring(MaLTC,4,10) as int)"
        self.tableWidget.setRowCount(50)
        tablerow=0
        # đặt số cột, và dữ liệu ở mỗi cột
        for row in ketnoi().execute(sqlquery):
            self.tableWidget.setItem(tablerow, 0, QtWidgets.QTableWidgetItem(row[0]))
            self.tableWidget.setItem(tablerow, 1, QtWidgets.QTableWidgetItem(row[1]))
            self.tableWidget.setItem(tablerow, 2, QtWidgets.QTableWidgetItem(row[2]))
            self.tableWidget.setItem(tablerow, 3, QtWidgets.QTableWidgetItem(str(row[3])))
            self.tableWidget.setItem(tablerow, 4, QtWidgets.QTableWidgetItem(str(row[4])))
            self.tableWidget.setItem(tablerow, 5, QtWidgets.QTableWidgetItem(row[5]))
            self.tableWidget.setItem(tablerow, 6, QtWidgets.QTableWidgetItem(row[6]))
            self.tableWidget.setItem(tablerow, 7, QtWidgets.QTableWidgetItem(self.searchTenMonHoc(str(row[7]))))
            tablerow+=1
        self.tableWidget.setRowCount(tablerow)
        self.tableWidget.setColumnWidth(7, 170)
        # Khong cho edit
        self.tableWidget.setEditTriggers(QtWidgets.QTableWidget.NoEditTriggers)

    # Hàm chuyển mã môn sang tên môn
    def searchTenMonHoc(self, a):
        sqlquery = "SELECT tenmh FROM monhoc WHERE mamh=N'" + a + "'"
        for row in ketnoi().execute(sqlquery):
            b = str(row).replace("(","",2)
            c = str(b).replace("'", "", 2)
            d = str(c).replace(",", "", 2)
            e = str(d).replace(")", "", 2)
        return e

    # Hàm chuyển tên môn sang mã môn
    def searchMaMonHoc(self, a):
        sqlquery = "SELECT mamh FROM monhoc WHERE tenmh=N'" + a + "'"
        for row in ketnoi().execute(sqlquery):
            b = str(row).replace("(", "", 2)
            c = str(b).replace("'", "", 2)
            d = str(c).replace(",", "", 2)
            e = str(d).replace(")", "", 2)
        return e

