    # Hàm tìm ra LTC lớn nhất -> đưa vào lable LTC
    def taoMaLTC(self):
        sqlquery = "select maltc from loptinchi"
        max=0
        for row in ketnoi().execute(sqlquery):
            b = str(row).replace("(", "", 2)
            c = str(b).replace("'", "", 2)
            d = str(c).replace(",", "", 2)
            e = str(d).replace(")", "", 2)
            ma = int((str(e)[3:5]))
            if ma>max:
                max=ma
        return "LTC"+str(max+1)

