    # Hàm tải dữ liệu
    def loaddata(self):
        sqlquery = "select * from sinhvien where masv='" + DBHelper.matk + "'"
        tablerow = 0
        # đặt số cột, và dữ liệu ở mỗi cột
        for row in ketnoi().execute(sqlquery):
            self.label_2.setText(row[1])
            self.label_3.setText(row[0])
            self.label_7.setText("Nam" if str(row[2]).__eq__("True") else "Nữ")
            self.label_4.setText(row[3])
            self.label_6.setText(row[4])
            self.label_5.setText(row[5])
            self.label_14.setText(self.searchTenCN(str(row[7])))
            self.label_15.setText(self.searchTenLop(str(row[8])))
            self.label_16.setText(row[10])

    # Hàm chuyển mãCN sang tênCN
    def searchTenCN(self, a):
        sqlquery = "SELECT tenCN FROM chuyennganh WHERE maCN='" + a + "'"
        for row in ketnoi().execute(sqlquery):
            b = str(row).replace("(", "", 2)
            c = str(b).replace("'", "", 2)
            d = str(c).replace(",", "", 2)
            e = str(d).replace(")", "", 2)
        return e

    # Hàm chuyển mã lớp sang tên lớp
    def searchTenLop(self, a):
        sqlquery = "SELECT tenlop FROM lop WHERE malop=N'" + a + "'"
        for row in ketnoi().execute(sqlquery):
            b = str(row).replace("(","",2)
            c = str(b).replace("'", "", 2)
            d = str(c).replace(",", "", 2)
            e = str(d).replace(")", "", 2)
        return e.upper()

